function launchMiniFireworks(t,e=50){if(!t)return;const o=document.querySelector("#surpriseModal .modal-content");if(!o)return;const n=["#56B300","#FFB000","#9CC105","#D5E44F","#F62D8D","#DDF0CC"],a=t.getBoundingClientRect(),r=o.getBoundingClientRect(),i=a.left-r.left+a.width/2,s=a.top-r.top+a.height/1.5;for(let t=0;t<e;t++){const t=document.createElement("span");t.className="sparkle";const e=1.5+Math.floor(4*Math.random());t.style.width=`${e}px`,t.style.height=`${e}px`,t.style.background=n[Math.floor(Math.random()*n.length)],t.style.left=`${Math.round(i)}px`,t.style.top=`${Math.round(s)}px`;const a=Math.random()*Math.PI,r=100+400*Math.random(),d=Math.cos(a)*r,l=-Math.sin(a)*r;t.style.setProperty("--x",`${d.toFixed(1)}px`),t.style.setProperty("--y",`${l.toFixed(1)}px`);const h=(.2*Math.random()).toFixed(2),c=(1.2+1*Math.random()).toFixed(2);t.style.animation=`miniFirework ${c}s cubic-bezier(.25,.6,.3,1) ${h}s forwards`,o.appendChild(t),t.addEventListener("animationend",()=>t.remove(),{once:!0})}}